	<style>
		.notification {
		position: fixed;
		top: 0;
		left: 0;
		right: 0;
		min-height: 40px;
		z-index: 10;
		height: auto;
		display: none;
		transition: all 0.2s, 0.2s;
		}
		.notification.visible-notification {
		display: block;
		transition: all 0.2s, 0.2s;
		}

		.notification-body {
		display: flex;
		justify-content: center;
		height: 100%;
		padding: 5px;
		align-items: center;
		}
		.notification-error .notification-body {
		background-color: #e32626de;
		}
		.notification-success .notification-body {
		background-color: #26e326ab;
		}
		.notification-text {
		font-size: 17px;
		text-transform: uppercase;
		font-family: Montserrat;
		text-align: left;
		word-break: break-all;
		}
	</style>
	<script src="https://cdn.jsdelivr.net/npm/axios@0.18.0/dist/axios.min.js"></script>
	<script>

    function ymClick(targetName){
      // href="";
      if (window.ym){
        ym(56224504, 'reachGoal', targetName);
      }
      location.href="/?itm=signup";
    }

		function showNotification(type, text) {
			let container = document.getElementById("notification");
			let visibilityClass = "visible-notification";
			let newClass =
				type == "error" ? "notification-error" : "notification-success";

			if (!container) {
				return false;
			} else {
				let textContainer = document.getElementById("notification-text");

				container.classList.add(visibilityClass);
				container.classList.add(newClass);
				textContainer.textContent = text;
				setTimeout(() => {
				container.classList.remove(visibilityClass);
				container.classList.remove(newClass);
				textContainer.textContent = "";
				}, 2000);
			}
		}
		function submitSearchForm(event) {
  			event.preventDefault();
  			event.stopPropagation();
  			let query = event.target["search"].value;
  			let url =`bids?type_deal=buy&date_published=desc&search=${query}&page=1`;
  			let link = document.createElement("a");
  			link.href = url;
  			link.dispatchEvent(new MouseEvent(`click`, { bubbles: true, cancelable: true, view: window }));
		}
		// оставить заявку без регистрации
		function additem(event) {
		event.preventDefault();
		event.stopPropagation();

		let data = {
			text: event.target["description"].value,
			email: event.target["email"].value,
			phone: event.target["phone"].value
			.replace(new RegExp(/['+','\-',' ','(',')']/, "g"), "")
			.replace("7", ""),
			agreement: event.target["agreement"].checked
		};

		// оставить заявку
		let url = "/api/v1/bladesorder";
			// console.log(data)
		axios
			.post(url, { data: data })
			.then(response => {
			if (response.data && response.data.result == true) {
				showNotification("success", "Спасибо за заявку");
			} else {
				let text = "Данный E-mail уже зарегистрирован";
				if (response.data.error == text) {
				showNotification(
					"error",
					" Вы уже зарегистрированы на сайте. Войдите под своей учетной записью."
				);
				} else {
				showNotification("error", "Произошла ошибка, попробуйте позднее");
				}
			}
			})
			.catch(e => {
			console.log(e);
			showNotification("error", "Произошла ошибка, попробуйте позднее");
			});
		}
		document.querySelectorAll('a[href^="#"]').forEach(anchor => {
			anchor.addEventListener("click", function(e) {
				e.preventDefault();

				document.querySelector(this.getAttribute("href")).scrollIntoView({
				behavior: "smooth"
				});
